import React, { Component } from 'react';
import axios from 'axios';



class BookReview extends Component {

    state = {
        book: {},
        reviews: [],
        title: '',
        review: ''
    }

    componentDidMount() {
        axios.get("http://127.0.0.1:5000/review/" + this.props.match.params.isbn)
        .then(res => this.setState({ book: res.data }))
    }

    addReview = (title, review) => this.setState({ reviews: [...this.state.reviews, title, review] });

    onChange = (e) => this.setState({ [e.target.name] : [e.target.value] })

    onSubmit = (e) => {
        e.preventDefault();
        this.addReview(this.state.title, this.state.review);
        this.setState({ title: '', review: '' });
    }

    render() {
        console.log(this.state.review)
        return (
            <div>
                <h3>Isbn: {this.state.book.isbn}</h3>
                <h3>Name: {this.state.book.title}</h3>
                <h3>Author: {this.state.book.author}</h3>
                <h3>Published on: {this.state.book.year}</h3>
                <form onSubmit={this.onSubmit}>
                    <h4>Title</h4>
                    <input type="text"
                        name="title"
                        value={this.state.title}
                        onChange={this.onChange}></input>

                    <h4>Review</h4>
                    <input type="Review"
                        name="review"
                        value={this.state.review}
                        onChange={this.onChange}></input>
                    <br></br>
                    <br></br>
                    <input type="submit"
                        value="submit"
                    ></input>
                </form>
            </div>
        )
    }
}

export default BookReview
